import {AiFillHome,AiFillBell,AiFillFire} from "react-icons/ai";
import "../Footer/footer.css";
import {io} from "socket.io-client";
import { useState } from "react";

export default function Header(){
    let socket=io();
    const token=JSON.parse(localStorage.getItem("token"));
    const [notofocationTrigger,setNotificationTrigger]=useState(false);

    socket.on("liked",()=>{
        setNotificationTrigger(true);
    })

    function viewNotifications(){
        window.location="/notifications";
        setNotificationTrigger(false)
    }
    return <footer>
        <AiFillHome size="30px" onClick={()=>window.location="/home"}/>
        {notofocationTrigger?<AiFillBell  size="30px" onClick={()=>window.location="/notifications"} color="red"/>:
        <AiFillBell  size="30px" onClick={()=>window.location="/notifications"} color="white"/>}
        <AiFillFire  size="30px"/>
        <img src={token.user.profilePhoto} alt="profilePhoto" onClick={()=>window.location="/profile/"+token.user._id}/>
    </footer>
}
