import "./post.css";
import {AiOutlineHeart,AiOutlineSend} from "react-icons/ai";
import {FaRegCommentAlt} from "react-icons/fa";
import {IoMdClose} from "react-icons/io";
import {useState} from "react";
import axios from "axios";
import { useSelector,useDispatch } from "react-redux";

export default function Post({post,user,dispatch}){

    const userPhoto=JSON.parse(post.user);
    console.log(userPhoto);

    const posts=useSelector(store=>store.posts);
    

    const [comment,setComment]=useState();
    function postComment(){
        if(typeof(comment)==="string" && comment.length>0){
            let commObj={
                postid:post._id,        
                user:user.user,
                comment:comment,
                date:Date.now()
            }
            
            dispatch({type:"ADDCOMMENT",payload:commObj});
            post.comments.push(commObj)
            console.log(posts)


            
            //axios.post("/postcomment",commObj)
        }
    
    }
    const [commentToggle,setCommentToggle]=useState(false)
    const commentsStyle=commentToggle ? {display:"block"}:{display:"none"};
    return <div className="post">
        
        return         <div className="all-comments" style={commentsStyle}>
            <IoMdClose size="32px" color="red" onClick={()=>setCommentToggle(false)}/>
           
            {post.comments.map(singleComment=>{
                 
                    <div className="single-comment">
                        <h4>opala</h4>
                        <p>{singleComment.comment}</p>
                    </div>
            })}
            {console.log(post)}
            </div>


        <div className="post-header">
            <span>
                <img src={userPhoto.profilePhoto}/>
                <h3>{userPhoto.username}</h3>
            </span>
            
        </div>
        <div className="image-container">
            <img src={post.photo}/>
        </div>
        <div className="reactions">
            <span>
                <AiOutlineHeart size="28px"/>
                <FaRegCommentAlt size="25px" onClick={e=>setCommentToggle(true)}/>
                <AiOutlineSend size="28px"/>
            </span>

        </div>
        <div className="comment">
            <textarea value={comment} onChange={e=>setComment(e.target.value)}/>
            <button onClick={()=>postComment()}>POST</button>
        </div>
        <div className="post-comments">
            <div className="single-comment">
                <h4>username</h4>
                <p>:Komentar sadasdas  sa dsa d asdadasdasdas</p>
            </div>
        </div>
        
    </div>
}
