import "./profile.css";
import Header from "../Header/header";
import Footer from "../Footer/footer";
import { useEffect,useState } from "react";
import axios from "axios";

export default function Profile(props){
    let token=JSON.parse(localStorage.getItem("token"));

   

    const[user,setUser]=useState();
    useEffect(()=>{
        let id=props.match.params.id;
        axios.post("/api/users/getprofile",{
            userid:id
        })
        .then(res=>{
            if(typeof(res.data)==="object"){
                setUser(res.data);
                
            }else{
                console.log("User not found.");
            }
        })

    },[])
 

    if(!user){
       
        return <div>Loading</div>
    }
    
    function follow(id){
        axios.post("/api/users/follow",{
            followedID:id,
            followerID:token.user._id
        })
        .then(res=>{
            if(res.data==="Followed"){
                setUser({...user,following:following.push(id)})
            }else{
                console.log("Problem");
            }
        })
    }

    return <div className="profile">
        <Header/>
        <div className="profile-header">
            <div className="profile-info">
                <span id="main">
                    <img src={user.profilePhoto} alt="profile"/>
                    <h2>{user.username}</h2>
                </span>

                <span>
                    <div>
                        <h5>Posts</h5>
                        <p>{user.posts.length}</p>
                    </div>
                    <div>
                        <h5>Followers</h5>
                        <p>{user.followers.length}</p>
                    </div>
                    <div>
                        <h5>Following</h5>
                        <p>{user.following.length}</p>
                    </div>
                </span>

            </div>
            {!user.followers.includes(token.user._id) ? <button id="follow-btn" onClick>FOLLOW</button>:
            <button id="unfollow-btn">UNFOLLOW</button>}
        </div>
        <div className="image-container">
            {user.posts.map(photo=>{
                return <img src={photo.photo} alt="post"/>
            })}
                
                <img src="/profile.jpg"/>
                <img src="/profile.jpg"/>
        </div>
        <Footer/>
    </div>
}
