import { useState } from "react";
import {IoAddCircleOutline} from "react-icons/io5";
import axios from "axios";
import "./newpost.css";

export default function NewPost(){
    const token=JSON.parse(localStorage.getItem("token"));
    const user=token.user;

  

    const [description,setDescription]=useState("")
    const[file,setFile]=useState();
    const formdata=new FormData();
    const[imgToggle,setImgToggle]=useState(false)
    function uploadPhoto(e){
        setFile(e.target.files[0]);

        formdata.append("file",file);
        setTimeout(3000)
        axios.post("/api/posts/uploadphoto",formdata)
        .then(res=>{
            if(res.data==="Uploaded"){
                setImgToggle(true);
                console.log(formdata)
            }else{
                setImgToggle(false);
            }
        })


    }

    const [alert,setAlert]=useState();
    const alertStyle=alert===undefined ? {display:"none"}:{display:"block",color:"red"};
    function uploadPhotoInfo(e){
        if(file===undefined){
            setAlert("Please select 1 picture");
            return false;
        }
        axios.post("/api/posts/uploadpost",{
            photo:"/uploads"+file.name,
            userid:user._id,
            description:description,
        })
        .then(res=>{
            if(res.data==="Uploaded"){
                window.location="/";
            }else{
                setAlert("Error:File is not uploaded successfully, try again.")
            }
        })
    }

    return <div className="Newpost">
        <div className="photo-upload">
            <IoAddCircleOutline color="royalblue" style={imgToggle ? {display:"none"}:{display:"block"}}/>
            {imgToggle===false ? <input required type="file" onChange={(e)=>uploadPhoto(e)}/>:
            <img src={"/uploads/"+file.name} alt="uploadedImage"/>}

        </div>
    	
        <div className="description-upload">
            <textarea value={description} onChange={e=>setDescription(e.target.value)} placeholder="Type description of yout photo..."></textarea>
            <button onClick={()=>uploadPhotoInfo()}>UPLOAD</button>
            <p style={alertStyle}>{alert}</p>
        </div>
        

    </div>
}
