export default function postReducer(state=[],action){
    switch(action.type){
        case "LOAD":
            return state=action.payload;
        case "ADDCOMMENT":
            let post=state.find(photo=>photo._id===action.payload.postid);
            post.comments.push(action.payload);
            return state; 
        case "DELETECOMMENT":
            state.find(photo=>photo._id===action.payload.postid).comments.filter(comm=>comm.comment===action.payload.comment);
            console.log(action.payload.comment)
            return state;
        default:
            return state;
    }
}
