import axios from "axios";
import { useState } from "react";
import "../Login/login.css";

export default function Login(){
    const [loginForm,setLoginForm]=useState({
        name:"",
        surname:"",
        username:"",
        email:"",
        password:"",
        confirmPassword:""
    })
    const [alert,setAlert]=useState(undefined);
    const alertStyle=alert===undefined ? {display:"none"}:{display:"block",color:"red"}


    function login(){
        axios.post("/api/users/login",{
            username:loginForm.username,
            password:loginForm.password
        })
        .then(res=>{
            if(typeof(res.data)==="object"){
                localStorage.setItem("user",JSON.parse(res.data));
                window.location="/";
            }else{
                setAlert("Invalid username or password.");
            }
        })
    }

    return <div className="login">
        <div className="form">
            <h1>EDBOOK REGISTER</h1>
            <span>
                <input type="text" placeholder="Surname" value={loginForm.surname} onChange={e=>setLoginForm({...loginForm,surname:e.target.value})}/>
                <input type="text" placeholder="Name" value={loginForm.name} onChange={e=>setLoginForm({...loginForm,name:e.target.value})}/>
                <input type="text" placeholder="Username" value={loginForm.username} onChange={e=>setLoginForm({...loginForm,username:e.target.value})}/>
                <input type="text" placeholder="Email" value={loginForm.email} onChange={e=>setLoginForm({...loginForm,email:e.target.value})}/>
                <input type="password" placeholder="Password" value={loginForm.password} onChange={e=>setLoginForm({...loginForm,password:e.target.value})}/>
                <input type="password" placeholder="Password" value={loginForm.confirmPassword} onChange={e=>setLoginForm({...loginForm,confirmPassword:e.target.value})}/>
            </span>

            <button className="btn" onClick={()=>alert()}><strong>LOGIN</strong></button>
            <p id="login-alert" style={alertStyle}>{alert}</p>
        </div>
    </div>
}
