import "./post.css";
import { AiOutlineHeart,AiOutlineSend} from "react-icons/ai";
import {FaRegCommentAlt} from "react-icons/fa";
import {IoMdClose} from "react-icons/io";
import {useEffect, useState} from "react";
import axios from "axios";
import {useSpring,animated} from "react-spring";
import {useDispatch, useSelector} from "react-redux";


export default function Post({post,userInfo,dispatch},props){
    var [single_post,setSingle_post]=useState(post);
    var [user,setUser]=useState(userInfo);
    const [commentToggle,setCommentToggle]=useState(false)
    const commentsStyle=useSpring(commentToggle ? {display:"block"}:{display:"none"});
    const [likeTrigger,setLikeTrigger]=useState(false);
    const [comment,setComment]=useState();

    let updatedPosts=useSelector(store=>store.posts);
    

    let token=JSON.parse(localStorage.getItem("token"));

    

   
    useEffect(()=>{
        if(!single_post || !user){
            axios.post("/api/posts/getpost",{
                postid:"61154bb9c115b226b8d762bc"
            })
            .then(res=>{
                
                setUser(token.user);
                setSingle_post(res.data)

              
            });



        }
    },[])
 
    if(!single_post || !user){
        return <div>Loading</div>
    }


    function postComment(){
        if(typeof(comment)==="string" && comment.length>0){
            let commObj={
                postid:single_post._id,        
                user:token.user,
                comment:comment,
                date:Date.now()
            }
            
            dispatch({type:"ADDCOMMENT",payload:commObj});
            axios.put("/api/posts/postcomment",commObj);

            setComment("");
        }
    
    }
      function deleteComment(comm){
        dispatch({type:"DELETECOMMENT",payload:comm});
        axios.put("/api/posts/deletecomment",comm)
        setCommentToggle(false);

    }

    function like(postid){
        let a=single_post.likes.find(id=>id===token.user._id);
        if(!a){
            axios.put("/api/posts/like",{
                postid:postid,
                userid:token.user._id
            })
            dispatch({type:"LIKE",payload:{userid:token.user._id,postid:postid}});
            setLikeTrigger(!likeTrigger)
        }else{
            axios.put("/api/posts/unlike",{
                postid:postid,
                userid:token.user._id
            })
            dispatch({type:"UNLIKE",payload:{postid:postid,userid:token.user._id}});
            setLikeTrigger(!likeTrigger)
           
        }

    }


    let postUser=JSON.parse(single_post.user)
 

    let singlePost=updatedPosts.find(photo=>photo._id===single_post._id);


    const userPhoto=JSON.parse(single_post.user);

    return <div className="post">
        
       <animated.div className="all-comments" style={commentsStyle}>
            <IoMdClose size="32px" color="red" onClick={()=>setCommentToggle(false)}/>
           
            {single_post.comments.map(singleComment=>{
                 
            return  <div className="single-comment">
                        <span>
                            <span onClick={()=>window.location="/profile/"+singleComment.user._id}>
                                <img src={singleComment.user.profilePhoto} alt="prodile"/>
                                <h4>{singleComment.user.username}</h4>
                            </span>
                            
                            <p>: {singleComment.comment}</p>
                        </span>
                        {token.user._id===postUser._id || singleComment.user._id===postUser._id ? <IoMdClose onClick={()=>deleteComment(singleComment)}/>:false}
                    </div>
            })}
           
            </animated.div>


        <div className="post-header">
            <span onClick={()=>window.location="/profile/"+userPhoto._id}>
                <img src={userPhoto.profilePhoto} alt="profile"/>
                <h3>{userPhoto.username}</h3>
            </span>
            
        </div>
        <div className="image-container">
            <img src={single_post.photo} alt="profile"/>
        </div>
        <div className="reactions">
            <span>
                {!singlePost.likes.find(like=>like===token.user._id)?<AiOutlineHeart size="28px" onClick={()=>like(single_post._id)} color="black"/>:
                <AiOutlineHeart size="28px" onClick={()=>like(single_post._id)} color="red"/>}
                <FaRegCommentAlt size="25px" onClick={()=>setCommentToggle(true)}/>
                <AiOutlineSend size="28px"/>
            </span>

        </div>
        <div className="comment">
            <textarea value={comment} onChange={e=>setComment(e.target.value)} placeholder="Write comment..."/>
            <button onClick={()=>postComment()}>POST</button>
        </div>
        <div className="post-comments">
            <div className="single-comment last-comment">
                <h4>{single_post.comments.length>0 ? single_post.comments[single_post.comments.length-1].user.username:" "}</h4>
                <p> {single_post.comments.length>0 ? ": "+single_post.comments[single_post.comments.length-1].comment:" "}</p>
            </div>
        </div>
        
    </div>
}
