import {IoMdClose} from "react-icons/io";
import { useState,useEffect } from "react";
import axios from "axios";
import "./search.css";

export default function Search({searchToggle}){
    let token=JSON.parse(localStorage.getItem("token"));
    const[user,setUser]=useState();
    useEffect(()=>{
        let id=token.user._id;
        axios.post("/api/users/getprofile",{
            userid:id
        })
        .then(res=>{
            if(typeof(res.data)==="object"){
                setUser(res.data);
                
            }else{
                console.log("User not found.");
            }
        })

    },[]) 
    if(!user){
        return <div>Loading...</div>
    }
    return <div className="search">
        <IoMdClose color="red" size="24px" onClick={()=>searchToggle(false)}/>
        <div className="single-user">
            <span>
                <img/>
                <h5>username</h5>
            </span>

           {!user.following.find(flw=>flw===true)?<button id="follow-btn">FOLLOW</button>:
           <button id="unfollow-btn">UNFOLLOW</button>}
        </div>
    </div>
}
